sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({ api, event, helpers, imports }) {

    var newIndex = parseInt(event.payload.screenIndex);
    console.log('Index', newIndex);

    var newSections = api.data.get_nextform_object.output.result.screens[newIndex].sections;
    console.log('Sections', newSections);

    var newFields = api.data.get_nextform_object.output.result.screens[newIndex].fields;
    console.log('Fields', newFields);

    api.setState('screenPointer', newIndex);
    api.setState('sections', newSections);
    api.setState('fields', newFields);
}
