sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({ api, event, helpers, imports }) {
    var screens = api.data.get_nextform_object.output.result.screens;
    api.setState('data', );
    api.setState('screenPointer', 0);
    api.setState('sections', screens[0].sections);
    api.setState('fields', screens[0].fields);
}
