sys_script_include.script:
var NFAPIHelper = Class.create();
NFAPIHelper.prototype = {
    initialize: function() {},
	generate: function (nextFormId) {
		var nfGr = new GlideRecord('x_snc_nf_nextform');

		if (nfGr.get(nextFormId)) {
			var generatorGr = nfGr.generator.getRefRecord();
			var evaluator = new GlideScopedEvaluator();
			var nf = evaluator.evaluateScript(generatorGr, 'script');
			return nf.toJSON();
		}
	},
	process: function process (nextFormId, payload) {
		var nfGr = new GlideRecord('x_snc_nf_nextform');

		if (nfGr.get(nextFormId)) {
			var payloadGr = nfGr.processor.getRefRecord();
			var evaluator = new GlideScopedEvaluator();
			evaluator.putVariable('nfData', payload);
			return evaluator.evaluateScript(payloadGr, 'script');
		}
	},
    type: 'NFAPIHelper'
};
